update ordinfo set PressEnd = current_date where (bh={{form_order_detail.sourceData.bh}});
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=11 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,8,9,10);
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=12 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,8,9,10,11) and d.qty &gt; 4;
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=13 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,8,9,10,11,12) and (d.qty &gt; 4) and (d.style like &quot;%jacket%&quot; or d.style like &quot;%coat%&quot; or d.style like &quot;%pant%&quot;);
