update ordinfo set EMBEnd = current_date,xgsj=current_timestamp where (bh={{form_order_detail.sourceData.bh}});
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=11 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,5,6,7);
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=12 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,5,6,7,11) and d.qty >4;
update ordinfo d join ztdm z on d.bh={{form_order_detail.sourceData.bh}} and z.bh=13 set d.zt = z.bh,d.ztmc=z.mc where d.zt in (1,2,5,6,7,11,12) and (d.qty>4) and (d.style LIKE "%jacket%" or d.style like "%coat%" or d.style like "%pant%");
